import React from 'react'
import { useState } from 'react';

export default function User() {
    const [formData, setFormData] = useState({
        name: '',
        login: '',
        password: '',
      });

      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
      };

      const handleSubmit = (e) => {
        e.preventDefault();
        // Aqui você pode adicionar lógica para enviar os dados do formulário para o servidor
        // Por exemplo, usando fetch ou axios.
        console.log('Dados do formulário:', formData);
      };

      return (
        <div className="container">
          <div className="row">
            <div className="col-md-6 offset-md-3" style={{marginBottom: '10px'}}>
              <h2 style={{textAlign: 'center'}}>Cadastro de Usuário</h2>
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <label htmlFor="name">Nome</label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    name="name"
                    placeholder='Digite seu nome'
                    value={formData.name}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="login">Login</label>
                  <input
                    type="text"
                    className="form-control"
                    id="login"
                    name="login"
                    placeholder='Digite seu nome de usuário'
                    value={formData.login}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="password">Senha</label>
                  <input
                    type="password"
                    className="form-control"
                    placeholder='Digite sua senha'
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                  />
                </div>
                <button type="submit" className="btn btn-primary" style={{marginTop:'15px'}}>Cadastrar</button>
              </form>
            </div>
          </div>
        </div>
      );}
